import React, { useState, useEffect } from "react";
import { Card, Grid } from "semantic-ui-react";
import { Container } from "semantic-ui-react";
import Buttons from "./Buttons";
import { Button } from "semantic-ui-react";

export default function Films() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  async function fetchFilms(url) {
    let res = await fetch(url)
    let data = await res.json();
    setData(data);
    setLoading(false);
  }
  useEffect(() => {


    fetchFilms("https://swapi.dev/api/films");
  }, []);

  function handleNext(){
    fetchFilms(data.next);
  }
  function handlePrev(){

  }

  return (
    <>
      <h1>Films</h1>
      <Grid columns={6}>
        {data.results?.map((films, i) => {
          return (
            <Grid.Column key={i}>
              <Card>
                <Card.Content>
                  <Card.Header>{films.title}</Card.Header>
                  <Card.Description>
                    <strong>Episode</strong>
                    <p>{films.episode_id}</p>
                    <strong>Opening Crawl</strong>
                    <p>{films.opening_crawl}</p>
                    <strong>Director</strong>
                    <p>{films.director}</p>
                    <strong>Producer</strong>
                    <p>{films.producer}</p>
                    <strong>Release date</strong>
                    <p>{films.release_date}</p>
                  </Card.Description>
                </Card.Content>
              </Card>
            </Grid.Column>
          );
        })}
      </Grid>
      <Container>
        <Button content="Back" icon="left arrow" labelPosition="left" onClick={handlePrev}/>
        <Button content="Next" icon="right arrow" labelPosition="right" onClick={handleNext}/>
      </Container>
    </>
  );
}
