import React, { useState, useEffect } from "react";
import { Card, Grid } from "semantic-ui-react";
import { Container, Dimmer, Loader } from "semantic-ui-react";
import { Button } from "semantic-ui-react";

export default function Planets() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  async function fetchData(url) {
    let res = await fetch(url);
    let data = await res.json();
    setData(data);
    setLoading(false);
  }
  useEffect(() => {
    fetchData("https://swapi.dev/api/planets");
  }, []);

  function handleNext() {
    if (data.next === null) {
    } else {
      fetchData(data.next);
    }
  }
  function handlePrev() {
    if (data.previous === null) {
    } else {
      fetchData(data.previous);
    }
  }
  return (
    <>
      <Container>
        {loading ? (
          <Dimmer active inverted>
            <Loader inverted>Loading...</Loader>
          </Dimmer>
        ) : (
          <Container>
            <h1>Planets</h1>
            <Grid columns={2}>
              {data.results?.map((planets, i) => {
                return (
                  <Grid.Column key={i}>
                    <Card
                      style={{
                        width: "250px",
                        height: "250px",
                      }}
                    >
                      <Card.Content>
                        <Card.Header>{planets.name}</Card.Header>
                        <Card.Description>
                          <strong>Rotation period</strong>
                          <p>{planets.rotation_period}</p>
                          <strong>Orbital rotation</strong>
                          <p>{planets.orbital_rotation}</p>
                          <strong>Diameter</strong>
                          <p>{planets.diameter}</p>
                          <strong>Climate</strong>
                          <p>{planets.climate}</p>
                          <strong>Gravity</strong>
                          <p>{planets.gravity}</p>
                          <strong>Diameter</strong>
                          <p>{planets.diameter}</p>
                          <strong>Terrain</strong>
                          <p>{planets.terrain}</p>
                          <strong>Surface water</strong>
                          <p>{planets.surface_water}</p>
                          <strong>Population</strong>
                          <p>{planets.population}</p>
                          {/* <strong>Residents</strong>
                          <p>{planets.residents}</p> */}
                        </Card.Description>
                      </Card.Content>
                    </Card>
                  </Grid.Column>
                );
              })}
            </Grid>
            <Container>
              <Button
                content="Back"
                icon="left arrow"
                labelPosition="left"
                onClick={handlePrev}
              />
              <Button
                content="Next"
                icon="right arrow"
                labelPosition="right"
                onClick={() => handleNext()}
              />
            </Container>
          </Container>
        )}
      </Container>
    </>
  );
}
